color: #070707;
@media (max-width:768px){
    font-size: calc(14px + (18 - 14) * ( (100vw - 425px) / (1300 - 425) ));
 }
 cursor: pointer;
    border-radius: 15px;
    transition: all 0.1s ease;
:hover{
    transform: scale(1.05);
}
`;

const NextBtn=styled.div`
width: 100%;
height: 100%;
display: flex;
align-items: center;
justify-content: space-between;
position: absolute;
