import styled from "styled-components";

const Portfolio = styled.div`
width: 100%;
background-color: #f6f6f6;
display: flex;
align-items: center;
justify-content: center;
gap: 20px;
`;

const PortfolioUl=styled.div`
width: 250px ;
height: 300px;
display: flex;
flex-direction: column;
align-items: center;
justify-content: flex-start;
padding: 10px 0px;
gap: 20px;
margin: 20px 0px;
border-radius: 25px;
`;
const PortfolioText=styled.div`
font-family: 'Gilroy-Medium';
font-size: 16px;
text-align: center;
color: #070707;
@media (max-width:768px){
    font-size: calc(14px + (16 - 14) * ( (100vw - 425px) / (1300 - 425) ));
 }
 `;

export {Portfolio,PortfolioUl,PortfolioText};
