import styled from "styled-components";

const Container=styled.div`
max-width: 80%;
margin: auto;
`;

const Portfolio = styled.div`
width: 100%;
background-color: #f6f6f6;
display: flex;
align-items: center;
justify-content: center;
`;

const PortfolioUl=styled.div`
width: 250px ;
height: auto;
display: flex;
align-items: center;
justify-content: center;
padding: 10px 0px;
gap: 20px;
margin: 20px 0px;
box-shadow: 0px 0px 50px #c1c1c1;
border-radius: 25px;
`;
const PortfolioText=styled.div`
font-family: 'Gilroy-Medium';
font-size: 16px;
text-align: center;
color: #070707;
@media (max-width:768px){
    font-size: calc(14px + (16 - 14) * ( (100vw - 425px) / (1300 - 425) ));
 }
 `;

export {Container ,Portfolio,PortfolioUl,PortfolioText};
